<script lang="ts">
  let modalOpen = false

  function handleModalOpen(evt: Event) {
    (evt.target as HTMLButtonElement).blur()
    modalOpen = true
  }

  function handleModalClose(evt: Event) {
    (evt.target as HTMLButtonElement).blur()
    modalOpen = false
  }
</script>
<div class="menu">
  <button on:click={handleModalOpen} class="modal-open">Menu</button>
</div>
